import './Sidebar.css';
import React, { useState } from "react";
import Select from 'react-select'
import "./App.css";
import "bootstrap/dist/css/bootstrap.css";


const Sidebar = () => {
    const years = [
      { value: "blues", label: "2020" },
      { value: "rock", label: "2021" },
      { value: "jazz", label: "2022" },
      { value: "orchestra", label: "2023" },
    ];

    const technologies = [
        { value: "blues", label: "React" },
        { value: "rock", label: "Javascript" },
        { value: "jazz", label: "HTML" },
        { value: "orchestra", label: "C#" },
      ];
    const [selectedOption, setSelectedOption] = useState("");
    var handleChange = (selectedOption) => {
        console.log(selectedOption);
        setSelectedOption(selectedOption.value);
      };
    
  
    return (
      <div>
        <div class='filter-bg'>
            <h2>Filter</h2>
        <div className="row">
            <div className="mt-4 p-5 me-auto w-25 float-left col-xs-6">
                <h4>Year</h4>
                <Select options={years} />
            </div>
            <div className="mt-4 mr-3 me-auto w-25 float-left col-xs-6">
                <h4>Year</h4>
                <Select options={years} />
            </div>
        </div>
        <div className="container">
            <div className="mt-4 me-auto w-50">
                <h4>Technologies</h4>
                <Select isMulti onChange={handleChange} options={technologies} />
                </div>
        </div>
        </div>
      </div>
    );
  };
  
  export default Sidebar;
